"use client"

import { useEffect, useRef, useState } from "react"
import profileImage from "../assets/images/uday rathod.jpeg"
function Hero() {
  const heroRef = useRef(null)
  const imageRef = useRef(null)
  const [displayText, setDisplayText] = useState("")
  const [isDeleting, setIsDeleting] = useState(false)
  const [loopNum, setLoopNum] = useState(0)
  const [typingSpeed, setTypingSpeed] = useState(150)

  const toRotate = [
    "Web Developer",
    "Frontend Developer",
    "React Developer",
    "UI Developer"
  ]
  const period = 1500

  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, typingSpeed);

    return () => clearInterval(ticker);
  }, [displayText, isDeleting, loopNum]);

  const tick = () => {
    let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updatedText = isDeleting 
      ? fullText.substring(0, displayText.length - 1)
      : fullText.substring(0, displayText.length + 1);

    setDisplayText(updatedText);

    if (isDeleting) {
      setTypingSpeed(50);
    }

    if (!isDeleting && updatedText === fullText) {
      setIsDeleting(true);
      setTypingSpeed(period);
    } else if (isDeleting && updatedText === '') {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setTypingSpeed(100);
    }
  };

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("animate-fade-in")
            if (entry.target === imageRef.current) {
              entry.target.classList.add("animate-float")
            }
          }
        })
      },
      { threshold: 0.1, rootMargin: "50px" }
    )

    const heroElement = heroRef.current
    const imageElement = imageRef.current

    if (heroElement) observer.observe(heroElement)
    if (imageElement) observer.observe(imageElement)

    return () => {
      if (heroElement) observer.unobserve(heroElement)
      if (imageElement) observer.unobserve(imageElement)
    }
  }, [])

  const scrollToAbout = () => {
    const aboutSection = document.querySelector("#about")
    if (aboutSection) {
      aboutSection.scrollIntoView({ behavior: "smooth" })
    }
  }

  return (
    <section
      id="home"
      className="d-flex align-items-center position-relative overflow-hidden"
      style={{ 
        minHeight: "100vh"
      }}
    >
      <div className="container hero-container-enhanced">
        <div className="row align-items-center">
          {/* Image Column */}
          <div className="col-lg-6 col-md-12 mb-4 mb-lg-0">
            <div
              ref={imageRef}
              className="position-relative image-container"
              style={{ 
                perspective: "1000px",
                transformStyle: "preserve-3d"
              }}
            >
              <div
                className="rounded-circle overflow-hidden border border-4 border-primary shadow-lg profile-image"
                style={{
                  width: "280px",
                  height: "280px",
                  margin: "0 auto",
                  transform: "rotateY(0deg)",
                  transition: "all 0.5s ease",
                  position: "relative",
                  zIndex: "1",
                  boxShadow: "0 8px 32px 0 rgba(74,144,226,0.25)"
                }}
              >
                <img
                  src={profileImage}
                  alt="Profile"
                  className="w-100 h-100 object-fit-cover"
                  style={{ transform: "scale(1.05)" }}
                />
              </div>
              {/* Animated background elements */}
              <div className="animated-bg">
                <div className="circle circle-1"></div>
                <div className="circle circle-2"></div>
                <div className="circle circle-3"></div>
              </div>
              {/* Floating particles */}
              <div className="particles">
                {[...Array(3)].map((_, i) => (
                  <div key={i} className={`particle particle-${i + 1}`}></div>
                ))}
              </div>
            </div>
          </div>

          {/* Content Column */}
          <div className="col-lg-6 col-md-12">
            <div ref={heroRef} className="hero-content-enhanced">
              <h1 className="display-3 fw-bold mb-3 hero-title">
                <span className="text-primary">Hello, I'm </span>
                <span className="text-gradient">UDAY RATHOD</span>
              </h1>
              <div className="fs-2 fw-medium mb-4">
                <div className="typing-container" style={{ borderRight: 'none' }}>
                  <span className="typing-text text-accent-gradient">{displayText}</span>
                  <div className="typing-cursor"></div>
                </div>
              </div>
              <p className="fs-5 text-muted mb-4 hero-desc" style={{ maxWidth: "600px" }}>
                I create <span className="text-accent-gradient">beautiful</span> and <span className="text-accent-gradient">functional</span> web applications with modern technologies.<br />Passionate about <span className="text-accent-gradient">clean code</span> and <span className="text-accent-gradient">user-centered design</span>.
              </p>
              <div className="d-flex flex-wrap gap-3 mb-4">
                <button
                  className="btn btn-gradient btn-lg rounded-pill px-4"
                  onClick={scrollToAbout}
                >
                  View My Work
                </button>
                <a
                  href="/resume.pdf"
                  download
                  className="btn btn-outline-gradient btn-lg rounded-pill px-4"
                  style={{ textDecoration: 'none' }}
                >
                  Download Resume
                </a>
                <button
                  className="btn btn-outline-primary btn-lg rounded-pill px-4"
                  onClick={() => {
                    const contactSection = document.querySelector("#contact")
                    if (contactSection) {
                      contactSection.scrollIntoView({ behavior: "smooth" })
                    }
                  }}
                >
                  Contact Me
                </button>
              </div>
              {/* Social Links */}
              <div className="d-flex gap-3">
                <a
                  href="https://github.com/Udayis"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="social-icon github"
                  aria-label="GitHub"
                >
                  <i className="bi bi-github"></i>
                </a>
                <a
                  href="https://www.linkedin.com/in/uday-rathod-818293300?utm_source=share&utm_campaign=share_via&utm_content=profile&utm_medium=android_app"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="social-icon linkedin"
                  aria-label="LinkedIn"
                >
                  <i className="bi bi-linkedin"></i>
